-- Problem Teamleiter von RA9 scheint auf Abnahme nicht Herr Hombach zu sein, sondern Mehrlich,Lars

declare 

  FUNCTION fk_get_tl_von_team(i_team_kuerzel IN VARCHAR2)

   RETURN   NUMBER

   IS



   CURSOR crgetTLeiter (i_team_kuerzel VARCHAR2) IS

     SELECT onl.nummer * 100 + ot.nummer nl_team

                   FROM   org_teams  ot

                          JOIN org_niederlassungen onl ON (onl.id = ot.v_onl_id)

                   WHERE  ot.kuerzel = i_team_kuerzel;

     TLeiter     NUMBER;

     n_pers_nr   NUMBER;

   BEGIN

      n_pers_nr := NULL;



     OPEN crgetTLeiter(i_team_kuerzel);

      FETCH crgetTLeiter INTO TLeiter;

      IF crgetTLeiter%FOUND THEN

        CLOSE crgetTLeiter;

           n_pers_nr := TO_NUMBER(pk_per0_std.fk_teamleiter_pers_nr(TLeiter));

           RETURN n_pers_nr;

        ELSE

        CLOSE crgetTLeiter;

          RETURN null;

      END IF;



     END fk_get_tl_von_team;

  BEGIN 

    dbms_output.put_line('fk_get_tl_von_team(RA9)'||fk_get_tl_von_team('RA9'));

  END;

